function Collection[Domain.ActorNames] ListPossibleMatches(bool offers) 
{
		Domain.ListProducersMaterialRequest req;
		Domain.ListProducersMaterialStatements stat;

		if(offers)
		{
			stat.Statement = "MATCH p=(:Actor{Id: $props.ActorId})-[*]->(:Material)-[*]->(a:Actor) RETURN a";
		}
		else
		{
			stat.Statement = "MATCH p=(a:Actor)-[*]->(:Material)-[*]->(:Actor {Id: $props.ActorId}) RETURN a";

		}

		stat.Parameters = Domain.ListProducersMaterialParameters.Create();
		stat.Parameters.Properties = Domain.ListProducersMaterialProps.Create();

		stat.Parameters.Properties.ActorId = this.Id;

		req.Statements.Add(stat);

		var reqParsed = DataTransformations.KnowledgeBase.ListProducersMaterialRequest_To_ListProducersMaterialRequest(req);

		CommonLib.Serializer[Interfaces.KnowledgeBase.ListProducersMaterialRequest] ser;
		DebugLib.Logger.WriteInfoLine("request " +  ser.ToJson(reqParsed));

		Interfaces.KnowledgeBase.KnowledgeBaseResult result = Interfaces.KnowledgeBase.API.ListPossibleMatches(reqParsed);

		CommonLib.Serializer[Interfaces.KnowledgeBase.KnowledgeBaseResult] serRes;
		DebugLib.Logger.WriteInfoLine("result " +  serRes.ToJson(result));

		Collection[Domain.ActorNames] names;
		if(result.Results.Length > 0 && result.Results.Get(0).Data.Length > 0)
		{
			for int i = 0; i < result.Results.Get(0).Data.Length; i + 1 
			{
				Domain.ActorNames name;
				name.Name = result.Results.Get(0).Data.Get(i).Row.Get(0).Name;
				names.Add(name);
			}	
		}
		return names;
}

function bool IsInRole(string roleName)
{
	return this.Roles.Any(r => r.Name == roleName);
}

function bool HasPermission(string permission)
{
  bool hasPermissionfromRoles = this.Roles.Any(rr => rr.Permissions.Any(pp => pp.Name == permission));
	return hasPermissionfromRoles || this.Permissions.Any(pp => pp.Name == permission);
}

function string GetFullAddress()
{
	return this.StreetName + " " + this.Number + ",<br />" + this.Town + " " + this.Zip + "<br />" + this.Country.Name;
}

function string GetAbsolutePath() {                   
                  if (this.StorageMedium == Domain.StorageMedium.DataBase) { return null; }

                  string relativePath = FileIOLib.Path.Combine(this.FolderPath, this.FileName);
   	              return CommonLib.Utilities.GetUploadsPath(relativePath); 
            }

function Collection[byte] GetThumbnail(int width, int height) { 
              if (this.StorageMedium == Domain.StorageMedium.DataBase) { 
                return CommonLib.Utilities.CreateThumbnail(this.Blob, width, height);  
              }
              else {
                var image = FileIOLib.File.ReadAllBytesFrom(this.GetAbsolutePath());
                return CommonLib.Utilities.CreateThumbnail(image.ToCollection(), width, height);  
              }

            }
