static function void CreateKnowledgeProcess(Domain.Process process)
{
	if Application.Settings.DontCallKnowledgeGraph == "true"
	{
		return;
	}

	Domain.ProcessCreateRequest req;


	req.Statements.Add(Domain.ProcessBackend.CreateProcessStatement(process));

	var reqParsed = DataTransformations.KnowledgeBase.ProcessCreateRequest_To_ProcessCreateRequest(req);
	Interfaces.KnowledgeBase.API.CreateProcess(reqParsed);

}

static function void CreateKnowledgeProcessPlus(Domain.Process process, bool edit) 
{
	if Application.Settings.DontCallKnowledgeGraph == "true"
	{
		return;
	}

	Domain.ProcessCreateRequest req;

	req.Statements.Add(Domain.ProcessBackend.CreateProcessStatement(process));

	var source = process.Source.First();
	req.Statements.Add(Domain.ProcessBackend.CreateConvertByStatement(process, source));

	var product = process.Product.First();
	req.Statements.Add(Domain.ProcessBackend.CreateConvertedByStatement(process, product));

	var reqParsed = DataTransformations.KnowledgeBase.ProcessCreateRequest_To_ProcessCreateRequest(req);
	Interfaces.KnowledgeBase.API.CreateProcess(reqParsed);
}

static function Domain.ProcessCreateStatements CreateProcessStatement(Domain.Process process) 
{
	Domain.ProcessCreateStatements stat;
	stat.Statement = "CREATE (n:Process $props) RETURN n";
	stat.Parameters = Domain.ProcessCreateParameters.Create();
	stat.Parameters.Props = Domain.ProcessCreateProps.Create();
	stat.Parameters.Props.Id = process.Id;
	stat.Parameters.Props.Name = process.Name;
	stat.Parameters.Props.Notes = process.Notes;
	stat.Parameters.Props.Ref = process.Ref;
	return stat;
}

static function Domain.ProcessCreateStatements CreateConvertedByStatement(Domain.Process process, Domain.Material product) 
{
	Domain.ProcessCreateStatements stat;
	stat.Statement = "MATCH (m:Material {Id: $props.ProductId}) MATCH (p:Process {Id: $props.Id}) MERGE (p)-[rel:CONVERTED_BY]->(m) RETURN m";
	stat.Parameters = Domain.ProcessCreateParameters.Create();
	stat.Parameters.Props = Domain.ProcessCreateProps.Create();
	stat.Parameters.Props.Id = process.Id;
	stat.Parameters.Props.ProductId = product.Id;
	return stat;
}

static function Domain.ProcessCreateStatements CreateConvertByStatement(Domain.Process process, Domain.Material product) 
{
	Domain.ProcessCreateStatements stat;
	stat.Statement = "MATCH (m:Material {Id: $props.SourceId}) MATCH (p:Process {Id: $props.Id}) MERGE (m)-[rel:CONVERT_BY]->(p) RETURN m";
	stat.Parameters = Domain.ProcessCreateParameters.Create();
	stat.Parameters.Props = Domain.ProcessCreateProps.Create();
	stat.Parameters.Props.Id = process.Id;
	stat.Parameters.Props.SourceId = product.Id;
	return stat;
}

