        private Action<object, byte[], string, int[]> Update_ImageBox_FileBinding
        {
            get
            {
                return (object _model, byte[] bytes, string relativePath, int[] indexes) =>
                {
                    var @model = _model as ActorFormViewModel;
                    if(@model.Actor == null) @model.Actor = new DigicircMatchmaking.BO.Actor();
                    @model.Actor.ActorLogo = new BO.FileData()
                    {
                        Description = @model.Actor.ActorLogo?.Description
                    };
                    @model.Actor.ActorLogo.FileName = Path.GetFileName(relativePath);
                    @model.Actor.ActorLogo.FolderPath = Path.GetDirectoryName(relativePath);
                    @model.Actor.ActorLogo.Blob = bytes;
                    @model.Actor.ActorLogo.MaxFileSize = 0;
                    @model.Actor.ActorLogo.AllowedExtensions = "";
                    @model.Actor.ActorLogo.StorageMedium = BO.StorageMedium.FileSystem;
                    @model.Actor.ActorLogo.UploadDateTime = DateTime.Now;
                    @model.Actor.ActorLogo.UploadedBy = IdentityHelper.GetCurrentUserName();
                };
            }
        }

        private Func<object, int[], string> Get_ImageBox_FolderPath
        {
            get
            {
                return (object _model, int[] indexes) =>
                {
                    var @model = _model as ActorFormViewModel;
                    return @model.Actor?.DefaultFolderPathActorLogo();
                };
            }
        }

        private Func<object, int[], string> Get_ImageBox_RelativePath
        {
            get
            {
                return (object _model, int[] indexes) =>
                {
                    var @model = _model as ActorFormViewModel;
                    var _folderPath = Get_ImageBox_FolderPath(_model, indexes);
                    return Path.Combine(_folderPath, @model.Actor.ActorLogo.FileName);
                };
            }
        }

