        [HttpPost]
        [Route("ImageBox_Upload")]

        [CustomControllerActionFilter(HasDefaultResultView=true, ActionName = "GeneralOperation", ClaimType = ClaimTypes.GenericAction)]
        public ActionResult ImageBox_Upload()
        {
            var postedData = _LoadViewModel();
            var indexes = postedData["indexes"] == null
                          ? null
                          : postedData["indexes"].ToString()
                          .Split(new char[] { '_' }, StringSplitOptions.RemoveEmptyEntries)
                          .Select(x => int.Parse(x))
                          .ToArray();
            var __pendingFile = ExecuteImageBox_Upload(indexes, HttpContext.Request.Files);
            Update_ImageBox_FileBinding(model, __pendingFile.BinaryContents, __pendingFile.FileName, indexes);
            var __result = Json(new
            {

                Type = "FileUpload",
                FileName = __pendingFile.FileName,
                UploadedBy = @model.Actor.ActorLogo?.UploadedBy,
                Description = @model.Actor.ActorLogo?.Description,
                UploadDateTime = @model.Actor.ActorLogo?.UploadDateTime.ToString(),
                FolderPath = @model.Actor.ActorLogo?.FolderPath,
                ImageData = Convert.ToBase64String(FileHelper.Thumbnails.GetThumbnail(@model.Actor.ActorLogo?.Id, @model.Actor.ActorLogo?.Blob, 0, 0, false))
            }, JsonRequestBehavior.AllowGet);
            __result.MaxJsonLength = int.MaxValue;
            return __result;
        }

        public FileHelper.UploadInfo ExecuteImageBox_Upload(int[] indexes, HttpFileCollectionBase files)
        {
            var file = files[0];
            var pendingFile = new FileHelper.UploadInfo(file, "ActorForm", "ImageBox", indexes);
            pendingFile.GetRelativePath = Get_ImageBox_RelativePath;
            pendingFile.UpdateBinding = Update_ImageBox_FileBinding;
            IsDirty = true;
            FileHelper.PendingUploadInstance.Add(pendingFile);
            return pendingFile;
        }


